mapper.insert:
INSERT INTO site_link(
	  site_srl
	, link_cd
	, link_nm
	, link_url
	, link_cls
	, link_mtd_lst
	, link_mtd_cts
	, batch_itv
	, use_yn
	, reg_dt
	, upt_dt
)VALUES(
	  #{siteSrl}
	, #{linkCd}
	, #{linkNm}
	, #{linkUrl}
	, #{linkCls}
	, #{linkMtdLst}
	, #{linkMtdCts}
	, #{batchItv}
	, #{useYn}
	, SYSDATE()
	, SYSDATE()
)

mapper.update:
UPDATE site_link SET
	 link_cd = #{linkCd}
	, site_srl = #{siteSrl}
	, link_nm = #{linkNm}
	, link_url = #{linkUrl}
	, link_cls = #{linkCls}
	, link_mtd_lst = #{linkMtdLst}
	, link_mtd_cts = #{linkMtdCts}
	, batch_itv = #{batchItv}
	, use_yn = #{useYn}		
	, upt_dt	= SYSDATE()
WHERE link_srl = #{linkSrl}
