mapper.select:
SELECT role_srl ,role_cd ,role_nm ,use_yn FROM auth_role WHERE 1=1
AND use_yn = #{useYn}
ORDER BY role_srl DESC

mapper.update:
UPDATE  auth_role SET
	role_cd = #{roleCd}
	,role_nm = #{roleNm}
	,use_yn = #{useYn}
WHERE role_srl = #{roleSrl}

UPDATE  auth_role SET
	use_yn = #{useYn}
WHERE role_srl = #{roleSrl}
